-- Mayors Function 

create or replace function add_value_in_mayors_table 
(name_of_county_muncipality_or_city_mayor text, admin_unit_id int,
function text, year int, person_id int, function_id int)
returns setof mayors as $$
begin
-- inserting new values
function, year, person_id, function_id)
values (name_of_county_muncipality_or_city_mayor, admin_unit_id,
function, year, person_id, function_id);
-- select all 
return query select * from mayors;
end;
$$ language plpgsql;

